<?php
	class news
	{
		public static function listAll()
		{
			$request = new BDD();
			
			$sql = "SELECT * FROM news WHERE 1 ORDER BY id DESC";
			$values = array();
			
			$data = $request->_request($sql, $values);
			
			$data = array_chunk($data, 4);
			
			foreach($data as $k => $v)
			{
				echo "<tr>";
				
				foreach($v as $k2 => $v2)
				{
					echo "
						<td onclick='news.view(\"{$v2["id"]}\")'>
							<p class='background' style='background: white url(\"{$v2["image"]}\") no-repeat center;background-size: auto 100%;'><a class='tag {$v2["tags"]}'></a></p>
							<p class='title'><span>{$v2["title"]}<br /><o style='font-size: 1.5vh;'>(News n°{$v2["id"]})</o></span></p>
							<p class='other'><span><img src='public/images/news/date.png' /> {$v2["date"]}</span><span><img src='public/images/news/comments.png' /> 0</span></p>
						</td>
					";
				}
				
				echo "</tr>";
			}
		}
		
		public static function listMin()
		{
			$request = new BDD();
			
			$sql = "SELECT * FROM news WHERE 1 ORDER BY id DESC LIMIT 3";
			$values = array();
			
			$data = $request->_request($sql, $values);
			
			for($i = 0; $i < count($data); $i++)
			{
				echo "<span onclick='index.viewNews({$data[$i]["id"]});'><p>{$data[$i]["title"]}</p></span>";
			}
		}
		
		public static function add($params)
		{
			$params = base64_decode($params);
			
			$title = explode("////", $params)[0];
			$content = rawurldecode(base64_decode(explode("////", $params)[1]));
			$tag = explode("////", $params)[2];
            
            var_dump($content);
			
			$request = new BDD();
			
			// Vérification des permissions
			if(!$request->_isMod() && !$request->_isAdmin() && !$request->_isCA() && !$request->_isResp()) die("@error1");
			
			if($title != "" && $content != "" && $tag != "")
			{
				$doc = new DOMDocument('UTF-8');
				libxml_use_internal_errors(true);
				$doc->loadHTML($content);
				libxml_clear_errors();
				$xpath = new DOMXPath($doc);
				$src = $xpath->evaluate("string(//img/@src)");
				
				// On supprime les balises scripts du contenu
				$dom = new DOMDocument();
//				$dom->loadHTML(mb_convert_encoding($content, 'HTML-ENTITIES', 'UTF-8'));
				$dom->loadHTML($content);
				$script = $dom->getElementsByTagName('script');

				$remove = [];
				
				foreach($script as $item)
				{
				  $remove[] = $item;
				}

				foreach ($remove as $item)
				{
				  $item->parentNode->removeChild($item); 
				}

				$content = $dom->saveHTML();
				
				// Récupération de l'image de fond (s'il n'y en a pas, nous en mettons une par défaut)
                $src = ($src != "") ? $src : "  data:image/png;base64,";
				
				$sql = "INSERT INTO news (id, title, image, author, content, tags, date) VALUES (?, ?, ?, ?, ?, ?, ?)";
				$values = array(
					null,
					htmlspecialchars($title),
					$src,
					$_SESSION['session']['name']." ".$_SESSION['session']['surname'],
					$content,
					$tag,
					date("d/m/Y")
				);
				
				$request->_request($sql, $values);
				
				die("@ok");
			}
			
			die("@error2");
		}
		
		public static function view($params)
		{
			$id = base64_decode($params);
			
			$request = new BDD();
			
			$sql = "SELECT * FROM news WHERE id = ?";
			$values = array($id);
			
			$return = $request->_request($sql, $values);
			
			die(json_encode($return));
		}
		
		public static function update($params)
		{
			$params = base64_decode($params);
			
			$title = explode("////", $params)[0];
			$content = rawurldecode(base64_decode(explode("////", $params)[1]));
			$tag = explode("////", $params)[2];
			$id = explode("////", $params)[3];
			
			$request = new BDD();
			
			// Vérification des permissions
			if(!$request->_isMod() && !$request->_isAdmin() && !$request->_isCA() && !$request->_isResp()) die("@error");
			
			if($title != "" && $content != "" && $tag != "")
			{
				$doc = new DOMDocument('UTF-8');
				libxml_use_internal_errors(true);
				$doc->loadHTML($content);
				libxml_clear_errors();
				$xpath = new DOMXPath($doc);
				$src = $xpath->evaluate("string(//img/@src)");
				
				// On supprime les balises scripts du contenu (s'il y en a afin d'éviter la faille XSS)
				$dom = new DOMDocument();
//				$dom->loadHTML(mb_convert_encoding($content, 'HTML-ENTITIES', 'UTF-8'));
                $dom->loadHTML($content);
				$script = $dom->getElementsByTagName('script');

				$remove = [];
				
				foreach($script as $item)
				{
				  $remove[] = $item;
				}

				foreach ($remove as $item)
				{
				  $item->parentNode->removeChild($item); 
				}

				$content = $dom->saveHTML();
				
				// Récupération de l'image de fond (s'il n'y en a pas, nous en mettons une par défaut)
                $src = ($src != "") ? $src : "  data:image/png;base64,";
				
				$sql = "UPDATE news SET title = ?,image = ?,content = ?,tags = ? WHERE id = ?";
				$values = array(
					htmlspecialchars($title),
					$src,
					$content,
					$tag,
					$id
				);
				
				$request->_request($sql, $values);
				
				die("@ok");
			}
			
			die("@error");
		}
		
		public static function delete($params)
		{
			$id = base64_decode($params);
			
			$request = new BDD();
			
			// Vérification des permissions
			if(!$request->_isMod() && !$request->_isAdmin() && !$request->_isCA() && !$request->_isResp()) die("@error");
			
			$sql = "DELETE FROM news WHERE id = ?";
			$values = array($id);
			
			$request->_request($sql, $values);
		}
	}
?>